import { checkExceptions, createWaiter, WaiterState } from "@smithy/util-waiter";
import { DescribeRepositoryAssociationCommand, } from "../commands/DescribeRepositoryAssociationCommand";
const checkState = async (client, input) => {
    let reason;
    try {
        const result = await client.send(new DescribeRepositoryAssociationCommand(input));
        reason = result;
        try {
            const returnComparator = () => {
                return result.RepositoryAssociation.State;
            };
            if (returnComparator() === "Associated") {
                return { state: WaiterState.SUCCESS, reason };
            }
        }
        catch (e) { }
        try {
            const returnComparator = () => {
                return result.RepositoryAssociation.State;
            };
            if (returnComparator() === "Failed") {
                return { state: WaiterState.FAILURE, reason };
            }
        }
        catch (e) { }
        try {
            const returnComparator = () => {
                return result.RepositoryAssociation.State;
            };
            if (returnComparator() === "Associating") {
                return { state: WaiterState.RETRY, reason };
            }
        }
        catch (e) { }
    }
    catch (exception) {
        reason = exception;
    }
    return { state: WaiterState.RETRY, reason };
};
export const waitForRepositoryAssociationSucceeded = async (params, input) => {
    const serviceDefaults = { minDelay: 10, maxDelay: 120 };
    return createWaiter({ ...serviceDefaults, ...params }, input, checkState);
};
export const waitUntilRepositoryAssociationSucceeded = async (params, input) => {
    const serviceDefaults = { minDelay: 10, maxDelay: 120 };
    const result = await createWaiter({ ...serviceDefaults, ...params }, input, checkState);
    return checkExceptions(result);
};
